package com.liuwentao.bililottery.ServiceImpl;import com.liuwentao.bililottery.Entity.BiliWrapper;import com.liuwentao.bililottery.Service.BiliWrapperService;import com.liuwentao.bililottery.mappers.BiliWrapperMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by liuwentao on 2021/9/19 16:26 */@Servicepublic class BiliWrapperServiceImpl implements BiliWrapperService {    @Autowired    BiliWrapperMapper biliWrapperMapper;    @Override    public List<BiliWrapper> findLotteryHaveBeenDrawnByOpenId(String openId) {        return biliWrapperMapper.findLotteryHaveBeenDrawnByOpenId(openId);    }    @Override    public List<BiliWrapper> findLotteryNotDrawYetByOpenId(String openId) {        return biliWrapperMapper.findLotteryNotDrawYetByOpenId(openId);    }    @Override    public boolean replaceBiliWrapper(BiliWrapper biliWrapper) {        return biliWrapperMapper.replaceBiliWrapper(biliWrapper);    }}