package com.liuwentao.bililottery.ServiceImpl;import com.liuwentao.bililottery.Entity.PostLottery;import com.liuwentao.bililottery.Service.PostLotteryService;import com.liuwentao.bililottery.mappers.PostLotteryMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Date;import java.util.List;/** * Created by liuwentao on 2021/9/18 00:32 */@Servicepublic class PostLotteryServiceImpl implements PostLotteryService {    @Autowired    PostLotteryMapper postLotteryMapper;    @Override    public int replacePostLottery(PostLottery postLottery) {        return postLotteryMapper.replacePostLottery(postLottery);    }    @Override    public PostLottery findPostLotteryByOpenIdAndId(String openId, String id) {        return postLotteryMapper.findPostLotteryByOpenIdAndId(openId, id);    }    @Override    public List<PostLottery> findPostLotteryBetweenCurrentTimeAndNextTime(Date nextTime) {        return postLotteryMapper.findPostLotteryBetweenCurrentTimeAndNextTime(nextTime);    }}