package com.liuwentao.bililottery.util;import java.util.Date;/** * Created by liuwentao on 2021/9/16 21:13 * * flagSingleTon变量的单例，在服务端只有一个，它有一个属性nextTime用于标记下一次从数据库获取抽奖数据的时间 */public class NextTimeSingleTon {    private Date nextTime;    private static NextTimeSingleTon nextTimeSingleTon = new NextTimeSingleTon();    private NextTimeSingleTon(){}    public static NextTimeSingleTon getFlagSingleTon() {        return NextTimeSingleTon.nextTimeSingleTon;    }    public Date getFlag() {        return nextTime;    }    public void setFlag(Date nextTime) {        this.nextTime = nextTime;    }}